
public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char letter = 'A';
		
		switch(letter) {
		
		case 'A':
			System.out.println("It's an A");
			break;
			
		case 'B':
			System.out.println("It's an B");
			break;
			
		case 'C':
			System.out.println("It's an C");
			break;
			
		case 'D':
			System.out.println("It's an D");
			break;
			
		case 'E':
			System.out.println("It's an E");
			break;
			
		default:
			System.out.println("It's none of the first five letters");
		}
		
			
			
	}

}
